import React, { useEffect, useState } from "react";
import { Form, Col, Button } from "react-bootstrap";
import * as Yup from "yup";
import { Formik } from "formik";
import {
  Link,
  useNavigate,
  useParams,
  useSearchParams,
} from "react-router-dom";
import { toast } from "react-toastify";
import "./ForgotPassword.css";
import { FORGET_PASSWORD, RESET_PASSWORD } from "../../gql/userQueries";
import { useMutation } from "@apollo/client";
import { useDispatch } from "react-redux";

const ForgotPasswordSchema = Yup.object().shape({
  email: Yup.string().email("Invalid email").required("Required"),
  password: Yup.string()
    .min(5, "Atleast 6 characters long")
    .max(50, "Too Long")
    .required(),
  confirmPassword: Yup.string()
    .test("passwords-match", "Passwords must match", function (value) {
      return this.parent.password === value;
    })
    .required(),
});

const OtpSchema = Yup.object().shape({
  email: Yup.string().email("Invalid email").required("Required"),
});

const ForgotPassword = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [forgetPassword] = useMutation(FORGET_PASSWORD);
  const [mailSent, setMailSent] = useState(false);
  const [token, setToken] = useState(false);
  const [searchParams, setSearchParams] = useSearchParams();

  useEffect(() => {
    const _token = searchParams.get("token");
    if (_token) {
      setMailSent(true);
      setToken(_token);
    }
  }, []);

  const handleSubmit = ({ email }) => {
    forgetPassword({ variables: { input: { email } } })
      .then((result) => {
        const { errors, data } = result;
        if (data.forgetPassword.status === "success") {
          toast("Reset Link sent to your email.");
        }
      })
      .catch((error) => {
        toast.error(error.message);
      });
  };

  const getOtp = (values) => {
    setMailSent(true);
  };

  return (
    <>
      <div className="container py-4">
        <h2 className="fs-2 pb-3">Forgot Password</h2>
        <Formik
          validationSchema={mailSent ? ForgotPasswordSchema : OtpSchema}
          onSubmit={mailSent ? handleSubmit : handleSubmit}
          validateOnChange={false}
          initialValues={{
            email: "",
            password: "",
            confirmPassword: "",
          }}
        >
          {({
            handleSubmit,
            handleChange,
            handleBlur,
            values,
            touched,
            errors,
          }) => (
            <Form noValidate onSubmit={(event) => handleSubmit(event)}>
              {mailSent ? (
                <>
                  <Form.Group
                    as={Col}
                    sm="4"
                    className="mx-auto text-start mb-3"
                    controlId="validationFormik02"
                  >
                    <Form.Label className="pb-1">Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="password"
                      name="password"
                      value={values.password}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      isValid={touched.password && !errors.password}
                      isInvalid={touched.password && !!errors.password}
                    />

                    <Form.Control.Feedback type="invalid">
                      {errors.password}
                    </Form.Control.Feedback>

                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                  <Form.Group
                    as={Col}
                    sm="4"
                    className="mx-auto text-start mb-3"
                    controlId="validationFormik02"
                  >
                    <Form.Label className="pb-1">Confirm Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="confirm password"
                      name="confirmPassword"
                      value={values.confirmPassword}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      isValid={
                        touched.confirmPassword && !errors.confirmPassword
                      }
                      isInvalid={
                        touched.confirmPassword && !!errors.confirmPassword
                      }
                    />

                    <Form.Control.Feedback type="invalid">
                      {errors.confirmPassword}
                    </Form.Control.Feedback>

                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                </>
              ) : (
                <>
                  <Form.Group
                    as={Col}
                    sm="4"
                    className="mx-auto text-start mb-3"
                    controlId="validationFormik01"
                  >
                    <Form.Label className="pb-1">Email *</Form.Label>
                    <Form.Control
                      type="email"
                      disabled={mailSent}
                      placeholder="example@example.com"
                      name="email"
                      value={values.email}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      isValid={touched.email && !errors.email}
                      isInvalid={touched.email && !!errors.email}
                    />
                    {touched.email && errors.email && (
                      <Form.Control.Feedback type="invalid">
                        {errors.email}
                      </Form.Control.Feedback>
                    )}
                    <Form.Control.Feedback type="valid">
                      Valid email
                    </Form.Control.Feedback>
                  </Form.Group>
                </>
              )}

              <div>
                <Button
                  type="button"
                  onClick={(event) => handleSubmit(event)}
                  as={Col}
                  sm="4"
                  className="btn-signup btn btn-dark my-3 mx-auto"
                >
                  {!mailSent ? "Forgot Password" : "Reset Password"}
                </Button>
              </div>
            </Form>
          )}
        </Formik>
      </div>
    </>
  );
};

export default ForgotPassword;
